import React, { useState } from 'react';
import Display from './Display';
import Button from './Button';
import './Calculator.css';

function Calculator() {
  const [history, setHistory] = useState(''); // İşlem satırı
  const [result, setResult] = useState(''); // Sonuç
  const [pastResults, setPastResults] = useState([]); // Geçmiş işlemler
  const [darkMode, setDarkMode] = useState(false); // Dark/Light mode
  const [showHistory, setShowHistory] = useState(false); // Geçmiş işlemler görünürlüğü

  // Buton tıklama işlemleri
  const handleButtonClick = (label) => {
    if (label === '=') {
      try {
        const evaluatedResult = eval(history); // İşlemi hesapla
        setResult(evaluatedResult); // Sonucu güncelle
        setHistory(history + ' = ' + evaluatedResult); // Son işlem satırı

        // Geçmiş işlemleri güncelle, sadece son 3 işlem gösterilecek
        const newPastResults = [{ history, result: evaluatedResult }, ...pastResults];
        if (newPastResults.length > 3) newPastResults.pop();
        setPastResults(newPastResults);

      } catch {
        setResult('Error');
      }
    } else if (label === 'AC') {
      // Sıfırlama işlemi
      setHistory('');
      setResult('');
    } else if (label === 'Geçmiş') {
      // Geçmiş işlemler görünürlüğünü değiştir
      setShowHistory(!showHistory);
    } else {
      // Yeni girişleri işlem satırına ekle
      setHistory(history + label);
    }
  };

  return (
    <div className={`calculator ${darkMode ? 'dark' : ''}`}>
      {/* Light/Dark mode düğmesi */}
      <div className="mode-toggle">
        <button onClick={() => setDarkMode(!darkMode)}>{darkMode ? 'Light' : 'Dark'} Mode</button>
      </div>

      {/* Geçmiş işlemler göster/geçiş düğmesi */}
      <div className="history-toggle">
        <button onClick={() => setShowHistory(!showHistory)}>History</button>
      </div>

      {/* İşlem ve sonuç gösterimi */}
      <Display history={history} result={result} />

      {/* Hesap makinesi butonları */}
      <div className="buttons">
        {['AC', '+/-', '%', '/', '7', '8', '9', '*', '4', '5', '6', '-', '1', '2', '3', '+', '0', '.', '=', '(', ')', 'Geçmiş'].map((btn) => (
          <Button key={btn} label={btn} onClick={handleButtonClick} />
        ))}
      </div>

      {/* Geçmiş işlemler alanı */}
      {showHistory && (
        <div className="past-results">
          <h4>History</h4>
          {pastResults.map((item, index) => (
            <div key={index}>
              <div>{item.history}</div>
              <div>= {item.result}</div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default Calculator;
