import React, { useState } from 'react';
import Display from './Display';
import Button from './Button';
import './Calculator.css';

function Calculator() {
  const [history, setHistory] = useState(''); // Üst satır için (işlemler)
  const [result, setResult] = useState(''); // Alt satır için (sonuç)
  const [pastResults, setPastResults] = useState([]); // Son 3 işlem için geçmiş listesi

  // Buton tıklama işlemlerini ele alan fonksiyon
  const handleButtonClick = (label) => {
    if (label === '=') {
      try {
        const evaluatedResult = eval(history); // İşlemi hesapla
        setResult(evaluatedResult); // Sonucu göster

        // Yeni geçmiş kaydını ekle ve son 3 işlemi sakla
        const newPastResults = [{ history, result: evaluatedResult }, ...pastResults];
        if (newPastResults.length > 3) newPastResults.pop();
        setPastResults(newPastResults);

      } catch {
        setResult('Hata');
      }
    } else if (label === 'AC') {
      // Sıfırlama butonu
      setHistory('');
      setResult('');
    } else if (label === 'Geçmiş') {
      // Geçmiş işlemler için geçici bir açma/kapama özelliği ekleyebiliriz
      setResult('Geçmiş gösteriliyor');
    } else {
      // Her tıklama sonucunda işlem satırını güncelle
      setHistory(history + label);
    }
  };

  return (
    <div className="calculator">
      <Display history={history} result={result} /> {/* Üst ve alt satır gösterimi */}
      <div className="buttons">
        {/* Butonlar - sayılar, operatörler, parantez ve özel butonlar */}
        {['AC', '+/-', '%', '/', '7', '8', '9', '*', '4', '5', '6', '-', '1', '2', '3', '+', '0', '.', '=', '(', ')', 'Geçmiş'].map((btn) => (
          <Button key={btn} label={btn} onClick={handleButtonClick} />
        ))}
      </div>
      {/* Geçmiş işlemler bölümü */}
      <div className="past-results">
        <h4>Geçmiş İşlemler</h4>
        {pastResults.map((item, index) => (
          <div key={index}>
            <div>{item.history}</div>
            <div>= {item.result}</div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Calculator;
